    private void nextActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_nextActionPerformed
        // TODO add your handling code here:
        int index = menu.getSelectedIndex(); 
        
            next.setEnabled(false);
            
            if(index+1 == 4)
            {
                back.setVisible(false);
                next.setVisible(false);
            }
            
            if(index == 0 && getAONivel() != 0)
                next.setEnabled(true);
            if(index == 1 && getNivelMRV() != 0)
                next.setEnabled(true);
            if(index == 2 && getNivelMRM() != 0)
                next.setEnabled(true);
            
            menu.setSelectedIndex(index+1);
        //menu.setSelectedIndex(index+1);
        
        int total;
        String pontuacao = "";
        String trauma  = "";
        total = getAONivel() + getNivelMRV() + getNivelMRM();  
        
        if(total == 3)
            pontuacao = "Coma profundo sem qualquer resposta.";
        else if(total >= 4 && total < 7)
            pontuacao = " Coma profundo com provável comprometimento do diencéfalo.";
        else if(total >= 7 && total < 11)
            pontuacao = "Coma moderado.";
        else if(total >= 11 && total < 15)
            pontuacao = "Coma superficial.";
        else if(total == 15)
            pontuacao = "Normalidade.";
                
        if(total >= 3 && total <= 8)
                trauma = "Grave, com necessidade de intubação imediata.";
        else if(total >= 9 && total <= 12)
                trauma = "Moderado.";
        else if(total >= 13 && total <= 15)
                trauma = "Leve.";
        
        situacao.setText(pontuacao);
        classificacao.setText(trauma);
        totalPonto.setText(String.valueOf(total));
    }//GEN-LAST:event_nextActionPerformed
    
